import {all , takeEvery , put} from "redux-saga/effects"
import axios from "axios"
import {cart_axios} from "../index"

function *CartGenerator(){
  // first make and async task  and based on result dispatch necessary action

  yield put({
      type:"CART_FETCHING"
  })

  var response  = yield cart_axios({method:"post",
   url:"https://apifromashu.herokuapp.com/api/cakecart",
   data:{}
  }
  )

  if(response.data.data){
      yield put({
          type:"CART_FTECHED",
          payload:response.data.data
      })
      console.log("data  from cart items " , response)
  }else{
    yield put({
        type:"CART_FAILURE"
    })
  }
}

function *Cartoperation(action){
  console.log("<<<<<<<<<<<<<",action)
 var user={cakeid:action.payload}
 console.log("",user)
  yield put({
    type:"CARTQUT_FETCHING"
})
   
  var addcaketocart  = yield cart_axios(
        {method:"post",
     url:"https://apifromashu.herokuapp.com/api/removeonecakefromcart",
     data:user})
     console.log("datafromcart quantity",addcaketocart.data)
     if(addcaketocart.data.data){
    yield put({
        type:"CART_ADD",
        payload:addcaketocart.data.data
    })
}else{
  yield put({
      type:"CART_FAILURE"
  })
}
  
  
  }


function *CartSaga(){
  yield takeEvery('Cart_Items' , CartGenerator)
}
function *CartoperationSaga(){
    yield takeEvery('Cart_Opration' , Cartoperation)
  }

export default function *RootSaga(){

 yield all([CartSaga(),CartoperationSaga()])
}
