import { createStore ,combineReducers, applyMiddleware } from "redux";
import createSaga from "redux-saga"
import RootSaga from './saga'
import {Addcakedetail} from './reducers'
import {Showlogin} from './reducers'
import {cartitemslist} from './reducers'
import {cartitemsopertion} from './reducers'

var sagaMiddleware = createSaga()
var reducers = combineReducers({Addcakedetail,Showlogin,cartitemslist,cartitemsopertion})
var store = createStore(reducers,applyMiddleware(sagaMiddleware))

sagaMiddleware.run(RootSaga)
 export default store
