//reducer is a pure function whichhelp toupdate state there is method1.state 2. action

export var Addcart = function(state={Item:10},action){

    switch(action.type){
        case Add_Cart:
            {
                state={...state}
                state["Item"] +=1
                return state
            }
            default:return state
    }
}
