import axios from "axios"
import {Component, PureComponent} from "react"
import Loader from "react-loader-spinner"


class Signup extends PureComponent{
    constructor(){
        super()
        // initialising the state
        this.state = {
            name:"Ashu",
            loading:0
        }
        alert("?In Constructor")
    }
    componentDidMount(){
      alert("in did mount")
    }

    componentDidUpdate(){
        alert("in did update")

    }
    user = {}



     handleEmail = (event)=>{
        this.user.email = event.target.value
      }
    handlePassword = (event)=>{
         this.user.password = event.target.value
       }
       handleName = (event)=>{
        this.user.name= event.target.value
      }
    signup = (event)=>{
       // updating the state
        this.setState({
            loading:98
        })
       console.log("......................" , this.user) 
      let urldata= "https://apifromashu.herokuapp.com/api/register";
      axios({
          method:"post",
          url:urldata,
          data:this.user
      }).then((responce)=>{
          console.log(responce);
      },(error)=>{
        console.log(error);
      })
    

       event.preventDefault();
    }

    render(){
        alert("in render")
        // var loader
        // if(this.state.loading){
        //      loader =  <Loader
        //     type="ThreeDots"
        //     color="#00BFFF"
        //     height={100}
        //     width={100} //3 secs
        //   />
        // }
        // else{
        //     loader = null
        // }
       
        return (
            <div style={{width:"50%" , margin:"auto"}}>
                {this.state.loading}
               {this.state.loading &&  <Loader
            type="ThreeDots"
            color="#00BFFF"
            height={100}
            width={100} //3 secs
          />}
                <form>
                <h1>Signup Here</h1>
                <div class="form-group">
                    <label for="exampleInputEmail1">Name</label>
                    <input onChange={this.handleName} type="text" class="form-control" aria-describedby="emailHelp" placeholder="Enter email" />
                </div>
                <div class="form-group">
                    <label for="exampleInputEmail1">Email address</label>
                    <input onChange={this.handleEmail} type="email" class="form-control"  aria-describedby="emailHelp" placeholder="Enter email" />
                    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                </div>
                <div class="form-group">
                    <label for="exampleInputPassword1">Password</label>
                    <input onChange={this.handlePassword} type="password" class="form-control"  placeholder="Password" />
                </div>
                <div>
                <label className="errormessage">{this.state.errorMessage}</label>
                <button style={{float:"right"}} onClick={this.signup} type="submit" class="btn btn-primary">Signup</button>
                </div>
                </form>
            </div>
        )
    }
}

export default Signup
