import axios from 'axios';
import { useEffect, useState } from 'react';
import {CakeCard} from './Cake_card'
import Loader from "react-loader-spinner"

export function Cakelist(props){
    var [cakes ,setcakes]=useState([])
    var[loader,setloader]=useState(true)
    useEffect(()=>{
      let apiurl = "https://apifromashu.herokuapp.com/api/allcakes"

      axios(
        {
          method:'get',
         url:apiurl     }
      ).then((response)=>{
       
        setcakes(response.data.data)
        setloader(false)
      },(error)=>{
        console.log(error)
      })
    },[])

    return(
        <>
       

<div className="container-fluid  p-5 ">
{loader &&  <Loader className="w-100 h-100 bg-dark" style={{ flex: 1, justifyContent: "center", alignItems: "center" }}/>}
<div class="d-flex justify-content-around flex-wrap flex-row w-100">
{cakes.map((each,index)=>{
       
         return <CakeCard key={index} data={each} />
       })}

 </div>
 </div>
        </>
    );
}
