//reducer is a pure function whichhelp toupdate state there is method1.state 2. action

export var Addcartdetail = function(state={Item:10,amount:10000},action){

    switch(action.type){
        case "Add_Cart":
            {
                state={...state}
                state["Item"] +=1
                return state
            }
            case "remove_cart":
                {
                    state={...state}
                    state["Item"] -=1
                    state["amount"]-=10
                    return state
                }
            default:return state
    }
}
