import axios from "axios"
import { PureComponent } from "react"
import { connect } from "react-redux";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Navbar from '../Component/Navbar';
import { Orderplacethunk } from "../reduxstore/thunk"

class Orderplace extends PureComponent {
    constructor() {
        super()
        // initialising the state    
    }
    user = {cakes:this.props.cartcakeslist,
    price:this.props.cartprice}
    handleName = (event) => {
        this.user.name = event.target.value
    }
    handleAddress = (event) => {
        this.user.address = event.target.value
    }
    handleArea = (event) => {
        this.user.area = event.target.value
    }
    handleCity = (event) => {
        this.user.city = event.target.value
    }
    handlePincode = (event) => {
        this.user.pincode = event.target.value
    }
    handleMobileno = (event) => {
        this.user.phone = event.target.value
    }
  

    Orderplace = (event) => {
        console.log("order placed with price",this.user)
        // updating the state
        event.preventDefault();
        if (this.user.name == " " || this.user.name == undefined) {
            alert("Enter Your name")
        }

        else if ( this.user.address == " " ||  this.user.address == undefined) {
            alert("Enter Your Address")
        }
        else if (this.user.area  == " " || this.user.area  == undefined) {
            alert("Enter Your Area")
        }


        else if (!this.user.name == " " || this.user.name == undefined && !this.user.address == " " || this.user.address == undefined) {

            this.props.dispatch(Orderplacethunk(this.user))
            toast.success('Login Succsefully Done', {
                position: "top-left",
                autoClose: false,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
            this.props.history.push("/")
        }
     
    }

    render() {

        return (
            <>
                <Navbar></Navbar>
                <div className=" bg-light rounded p-5 mt-5 shadow" style={{ width: "50%", margin: "auto" }}>

                    <form className="mt-1">
                        <h2 className="text-center">Place Your Order</h2>
                        <div className="form-group">
                            <label for="exampleInputEmail1">Name</label>
                            <input onChange={this.handleName} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter Name" />
                        </div>
                        <div className="form-group">
                            <label for="exampleInputEmail1">Address</label>
                            <input onChange={this.handleAddress} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter address" />

                        </div>
                        <div className="form-group">
                            <label for="exampleInputPassword1">Area</label>
                            <input onChange={this.handleArea} type="text" className="form-control" placeholder="Enter area" />
                        </div>
                        <div className="form-group  mt-5">
                            <div className="input-group">
                                <label for="exampleInputEmail1" >City Name</label>
                                <input onChange={this.handleCity} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter City" />

                                <div className="form-check form-check-inline ml-5">
                                    <label for="exampleInputEmail1">Pincode</label>
                                    <input onChange={this.handlePincode} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter Pincode" />
                                </div>
                            </div>
                        </div>
                        <div className="form-group  mt-5">
                            <div className="input-group">
                                <label for="exampleInputEmail1">Mobile Number</label>
                                <input onChange={this.handleMobileno} type="text" className="form-control" aria-describedby="emailHelp"  placeholder="Enter Mobile No" />

                                <div className="form-check form-check-inline ml-5">
                                    <label for="exampleInputEmail1">Amount</label>
                                    <input type="text" className="form-control" aria-describedby="emailHelp" disabled="true" />
                                </div>
                            </div>
                        </div>
                        <div className="text-center">

                            <button onClick={this.Orderplace} type="submit" className="btn btn-primary mt-3">Place Order</button>
                            <ToastContainer
                                position="top-left"
                                autoClose={false}
                                newestOnTop={false}
                                closeOnClick
                                rtl={false}
                                pauseOnFocusLoss
                                draggable
                            />
                        </div>

                    </form>

                </div>
            </>
        )
    }
}

export default connect(function (state, props) {
    return {
        cartcakeslist: state["cartitemslist"]["cartitems"] || [],
        cartprice:state["cartitemslist"]["cartprice"]||[]

    }
})(Orderplacereducer)
