import {all , takeEvery , put,call} from "redux-saga/effects"
import axios from "axios"
import {cart_axios} from "../index"

function *CartGenerator(){
  // first make and async task  and based on result dispatch necessary action
var success = true
  yield put({
      type:"CART_FETCHING"
  })

  var response  = yield cart_axios({method:"post",
   url:"https://apifromashu.herokuapp.com/api/cakecart",
   data:{}
  }
  )

  if(response.data.data){
      yield put({
          type:"CART_FTECHED",
          payload:response.data.data
      })
      console.log("data  from cart items " , response)
  }else{
    yield put({
        type:"CART_FAILURE"
    })
  }
}

function *Cartoperation(action){
    // first make and async task  and based on result dispatch necessary action
   const cakeid = action.cakeid

   const payload = yield call(action.payload.cakeid);
   console.log("<<<<>>>cake",payload)
  var addcaketocart  = yield cart_axios(
        {method:"post",
     url:"https://apifromashu.herokuapp.com/api/addcaketocart",
     data:{cakeid}})
     if(addcaketocart.data.data){
    yield put({
        type:"CART_ADD",
        payload:addcaketocart.data.data
    })
}
  
    var removeonecakefromcart = yield cart_axios(
        {method:"post",
     url:"https://apifromashu.herokuapp.com/api/removeonecakefromcart",
     data:{}
    }
    )
    if(removeonecakefromcart.data.data){
        yield put({
            type:"CART_REMOVE",
            payload:removeonecakefromcart.data.data
        })
        console.log("data  from cart items " , removeonecakefromcart)
    }
    var removecakefromcart   = yield cart_axios(
        {method:"post",
     url:"https://apifromashu.herokuapp.com/api/removecakefromcart ",
     data:{}})
     if(removecakefromcart.data.data){
    yield put({
        type:"CART_DELETE",
        payload:removecakefromcart.data.data
    })
}
  }


function *CartSaga(){
  yield takeEvery('Cart_Items' , CartGenerator)
}
function *CartoperationSaga(){
    yield takeEvery('Cart_Opration' , Cartoperation)
  }

export default function *RootSaga(){

 yield all([CartSaga(),CartoperationSaga()])
}
