import axios from "axios"
import { PureComponent } from "react"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Navbar from '../Component/Navbar';

class Orderplace extends PureComponent {
    constructor() {
        super()
        // initialising the state    
    }
    user = {}

    handleEmail = (event) => {
        this.user.email = event.target.value
    }
    handlePassword = (event) => {
        this.user.password = event.target.value
    }
    handleName = (event) => {
        this.user.name = event.target.value
    }
    Orderplace = (event) => {
        // updating the state
        event.preventDefault();
        if (this.user.name == " " || this.user.name == undefined) {
            alert("Enter Your name")
        }

        else if (this.user.email == " " || this.user.email == undefined) {
            alert("Enter Your Email Id")
        }
        else if (this.user.password == " " || this.user.password == undefined) {
            alert("Enter Your password")
        }


        else if (!this.user.password == " " || this.user.password == undefined && !this.user.email == " " || this.user.email == undefined) {
            console.log("pass props to thunk")
        }
        else {

            toast.success('Login Succsefully Done', {
                position: "top-left",
                autoClose: false,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
            this.props.history.push("/")
        }
    }

    render() {

        return (
            <>
                <Navbar></Navbar>
                <div className=" bg-light rounded p-5 mt-5 shadow" style={{ width: "50%", margin: "auto" }}>

                    <form className="mt-1">
                        <h2 className="text-center">Place Your Order</h2>
                        <div className="form-group">
                            <label for="exampleInputEmail1">Name</label>
                            <input onChange={this.handleName} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter Name" />
                        </div>
                        <div className="form-group">
                            <label for="exampleInputEmail1">Address</label>
                            <input onChange={this.handleEmail} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter address" />

                        </div>
                        <div className="form-group">
                            <label for="exampleInputPassword1">Area</label>
                            <input onChange={this.handlePassword} type="text" className="form-control" placeholder="Enter area" />
                        </div>
                        <div className="form-group  mt-5">
                            <div className="input-group">
                                <label for="exampleInputEmail1">City :</label>
                                <input onChange={this.handleEmail} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter City" />

                                <div className="form-check form-check-inline ml-5">
                                    <label for="exampleInputEmail1">Pincode :</label>
                                    <input onChange={this.handleEmail} type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter Pincode" />
                                </div>
                            </div>
                        </div>
                        <div className="form-group  mt-5">
                            <div className="input-group">
                                <label for="exampleInputEmail1">Mobile Number :</label>
                                <input onChange={this.handleEmail} type="text" className="form-control" aria-describedby="emailHelp"  placeholder="Enter Mobile No" />

                                <div className="form-check form-check-inline ml-5">
                                    <label for="exampleInputEmail1">Amount :</label>
                                    <input onChange={this.handleEmail} type="text" className="form-control" aria-describedby="emailHelp" disabled="true" />
                                </div>
                            </div>
                        </div>
                        <div>

                            <button onClick={this.Orderplace} type="submit" className="btn btn-primary mt-3">Signup</button>
                            <ToastContainer
                                position="top-left"
                                autoClose={false}
                                newestOnTop={false}
                                closeOnClick
                                rtl={false}
                                pauseOnFocusLoss
                                draggable
                            />
                        </div>

                    </form>

                </div>
            </>
        )
    }
}

export default Orderplace
