import { useHistory } from 'react-router-dom';


export function CakeCard(props) {
    const history = useHistory();
   
    var [cakedetails , setCakedetails]  = useState({})
    useEffect(()=>{
        let apiurl = "https://apifromashu.herokuapp.com/api/cake/" + props.match.params.cakeid
    
        axios(
          {
            method:'get',
           url:apiurl    
         }
        ).then((response)=>{
          console.log("response from  cake details api" , response.data)
          setCakedetails(response.data.data)
        },(error)=>{
          console.log("error from all cakes api" , error)
        })
        
       }, [])
    function showcakedetail(e){ 
       
        //we can do it using serch query also where we pass data in url
        var url = "/Cakedetails?q="+props.data
        history.push(url);
       
    }
    return (
        <><div className = "container">
        
            <div className="row w-100">
            <div className="d-flex ">
            <div  className="card" >
                <img src={props.data.image} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{props.data.name}</h5>
                    <p className="card-text">{props.data.description}</p>
                    <button className="btn btn-primary" onClick={showcakedetail} >Cake Details</button>
                </div>
            </div>
            </div>
            </div>
            </div>
        </>
    );
}
